switch (foo)
{
    case 1:
        print("howdy");
        break;
    
    case "foo":
    case "bar":
        print("foobar");
    
    default:
        print("default");
}

var value;

switch (bar)
{
    case "one":
    case "two":
        value = "buckle my shoe";
        break;
    
    case "three":
    case "four":
        value = "lock the door";
        break;
    
    default:
        value = "whatever";
}
